<?php// These no longer even need to be under the document rootdefine('SCRIPT_DIR', $_SERVER['DOCUMENT_ROOT'] . '/public/js');$bundles = array(  'site' => array(    'jQuery-1.3.2.js',    'jquery.js',    'jquery.layout.js',    'jquery.jqGrid.min.js',    'jquery.tablednd.js',    'jquery.ui.all.js',    'jquery.jqGrid.js', 		'grid.locale-en.js',	'grid.base.js',	'grid.common.js',	'grid.formedit.js',	'grid.inlinedit.js',	'grid.celledit.js',	'grid.subgrid.js',	'grid.treegrid.js',	'grid.custom.js',	'grid.postext.js',	'grid.tbltogrid.js',	'grid.setcolumns.js',	'grid.import.js',	'jquery.fmatter.js',	'json2.js',	'JsonXml.js',	'jqModal.js',	'jqDnR.js',	'jquery.form.js',	'jquery.bgiframe.min.js',	'jquery.ajaxQueue.js',	'thickbox-compressed.js',	'jquery.autocomplete.js',  ),);$site = $_GET['site'];if (!isset($bundles[$site])) {  error_log("javascript.php: Unknown bundle '$site' requested");  exit;}header('Content-Type: text/javascript');foreach ($bundles[$site] as $file) {  if (@readfile(SCRIPT_DIR . $file) === false) {    error_log("javascript.php: Error reading file '$file'");  }}?>