<?if (!defined('BASEPATH')) exit('No direct script access allowed');class p_empcopy extends Controller {	function p_empcopy ()	{		parent::Controller();			/*modul yang di load halaman vehicle activity*/		$this->load->model( 'model_vehicle_activity' ); 		$this->load->helper('form');		$this->load->helper('language'); 		$this->load->helper('url');		$this->load->helper('object2array');        $this->load->library('form_validation');		$this->load->library('global_func');		$this->load->library('session');	}		function index()    {		$data = array();				$data['periode'] = $this->global_func->drop_date2('bulan','tahun','select');		$data['login_id'] = $this->session->userdata('LOGINID');		$data['company_name'] = $this->session->userdata('NCOMPANY_NAME');		$data['company_code'] = $this->session->userdata('DCOMPANY');		$data['company_dest'] = $this->session->userdata('DCOMPANY_NAME');				if ($data['login_id'] == TRUE){			$this->load->view('info_employee_copy', $data);		} else {			redirect('login');		}		    }    }?>